import React, { useState, useEffect } from "react";
import {
  Container,
  Typography,
  Box,
  TextField,
  Button,
  Paper,
  Grid,
  Divider,
  Alert,
  CircularProgress,
  Chip,
  Avatar,
  IconButton,
  Breadcrumbs,
  Link,
  InputAdornment,
} from "@mui/material";
import {
  Home,
  Person,
  Email,
  CalendarMonth,
  Visibility,
  VisibilityOff,
  Edit,
  Lock,
  Badge,
} from "@mui/icons-material";
import { useNavigate } from "react-router-dom";
import { useTranslation } from "react-i18next";
import { useAuth } from "../../context/AuthContext";
import { updateProfile, changePassword, IProfileUpdate, IPasswordChange } from "../../services/Auth";

export default function Profile() {
  const { t } = useTranslation();
  const { user, isLoggedIn, setUser } = useAuth();
  const navigate = useNavigate();

  // Profile form state
  const [firstName, setFirstName] = useState(user?.first_name || "");
  const [lastName, setLastName] = useState(user?.last_name || "");

  // Password form state
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  // UI state
  const [loading, setLoading] = useState(false);
  const [profileSuccess, setProfileSuccess] = useState("");
  const [profileError, setProfileError] = useState("");
  const [passwordSuccess, setPasswordSuccess] = useState("");
  const [passwordError, setPasswordError] = useState("");

  useEffect(() => {
    if (!isLoggedIn) {
      navigate("/login");
    }
  }, [isLoggedIn, navigate]);

  useEffect(() => {
    if (user) {
      setFirstName(user.first_name || "");
      setLastName(user.last_name || "");
    }
  }, [user]);

  const handleProfileSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setProfileError("");
    setProfileSuccess("");
    setLoading(true);

    try {
      const data: IProfileUpdate = {
        first_name: firstName,
        last_name: lastName,
      };
      const updatedUser = await updateProfile(data);
      setUser(updatedUser);
      setProfileSuccess(t("profile.updated"));
    } catch (err: any) {
      const key = err.response?.data?.detail;
      setProfileError(key ? t(key) : t("common.error"));
    } finally {
      setLoading(false);
    }
  };

  const handlePasswordSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setPasswordError("");
    setPasswordSuccess("");

    if (newPassword !== confirmPassword) {
      setPasswordError(t("profile.password_mismatch"));
      return;
    }

    if (newPassword.length < 8) {
      setPasswordError(t("profile.password_too_short"));
      return;
    }

    setLoading(true);

    try {
      const data: IPasswordChange = {
        current_password: currentPassword,
        new_password: newPassword,
      };
      await changePassword(data);
      setPasswordSuccess(t("profile.password_changed"));
      setCurrentPassword("");
      setNewPassword("");
      setConfirmPassword("");
    } catch (err: any) {
      const key = err.response?.data?.detail;
      setPasswordError(key ? t(key) : t("common.error"));
    } finally {
      setLoading(false);
    }
  };

  if (!user) {
    return (
      <Container sx={{ py: 4, textAlign: "center" }}>
        <CircularProgress />
      </Container>
    );
  }

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Typography variant="h4" gutterBottom>
        {t("profile.title")}
      </Typography>

      {/* User Info Card */}
      <Paper sx={{ p: 3, mb: 4 }}>
        <Box sx={{ display: "flex", alignItems: "center", gap: 2, mb: 2 }}>
          <Typography variant="h6">{user.email}</Typography>
          <Chip 
            label={user.role} 
            color={user.role === "admin" ? "primary" : "default"} 
            size="small" 
          />
        </Box>
        <Typography variant="body2" color="text.secondary">
          {t("profile.member_since")}: {new Date().toLocaleDateString()}
        </Typography>
      </Paper>

      <Grid container spacing={4}>
        {/* Profile Update Form */}
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              {t("profile.update_info")}
            </Typography>
            <Divider sx={{ mb: 2 }} />

            {profileError && (
              <Alert severity="error" sx={{ mb: 2 }}>
                {profileError}
              </Alert>
            )}
            {profileSuccess && (
              <Alert severity="success" sx={{ mb: 2 }}>
                {profileSuccess}
              </Alert>
            )}

            <Box component="form" onSubmit={handleProfileSubmit}>
              <TextField
                fullWidth
                label={t("auth.first_name")}
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                margin="normal"
              />
              <TextField
                fullWidth
                label={t("auth.last_name")}
                value={lastName}
                onChange={(e) => setLastName(e.target.value)}
                margin="normal"
              />
              <Button
                type="submit"
                variant="contained"
                fullWidth
                sx={{ mt: 2 }}
                disabled={loading}
              >
                {loading ? <CircularProgress size={24} /> : t("common.save")}
              </Button>
            </Box>
          </Paper>
        </Grid>

        {/* Password Change Form */}
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              {t("profile.change_password")}
            </Typography>
            <Divider sx={{ mb: 2 }} />

            {passwordError && (
              <Alert severity="error" sx={{ mb: 2 }}>
                {passwordError}
              </Alert>
            )}
            {passwordSuccess && (
              <Alert severity="success" sx={{ mb: 2 }}>
                {passwordSuccess}
              </Alert>
            )}

            <Box component="form" onSubmit={handlePasswordSubmit}>
              <TextField
                fullWidth
                type="password"
                label={t("profile.current_password")}
                value={currentPassword}
                onChange={(e) => setCurrentPassword(e.target.value)}
                margin="normal"
                required
              />
              <TextField
                fullWidth
                type="password"
                label={t("profile.new_password")}
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                margin="normal"
                required
              />
              <TextField
                fullWidth
                type="password"
                label={t("auth.confirm_password")}
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                margin="normal"
                required
              />
              <Button
                type="submit"
                variant="contained"
                fullWidth
                sx={{ mt: 2 }}
                disabled={loading}
              >
                {loading ? <CircularProgress size={24} /> : t("profile.change_password")}
              </Button>
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
}
