import React from "react";
import Cart from "../cart/Cart";
import { useAuth } from "../../../context/AuthContext";
import { useTranslation } from "react-i18next";
import LanguageSwitcher from "../../LanguageSwitcher";

export default function SubNav() {
  const { user, isLoggedIn, logout } = useAuth();
  const { t } = useTranslation();

  return (
    <nav id="sub-nav">
      <ul>
        {!isLoggedIn ? (
          <>
            <li>
              <a href="/register">{t("nav.register")}</a>
            </li>
            <li>{t("general.free_shipping")}</li>
            <div className="flex gap-4 items-center">
              <li>
                <a href="/login">{t("nav.login")}</a>
              </li>
              <LanguageSwitcher />
              <Cart />
            </div>
          </>
        ) : (
          <>
            <li>
              <a href="/profile" style={{ textDecoration: "none", color: "inherit" }}>
                {t("nav.welcome")}, {user?.first_name || user?.email}
              </a>
            </li>
            <li>{t("general.free_shipping")}</li>
            <div className="flex gap-4 items-center">
              {user?.role === "admin" && (
                <li>
                  <a href="/admin">{t("nav.admin")}</a>
                </li>
              )}
              <li>
                <a href="/profile">{t("nav.account")}</a>
              </li>
              <li>
                <button onClick={logout} style={{ cursor: "pointer", background: "none", border: "none", color: "inherit" }}>
                  {t("nav.logout")}
                </button>
              </li>
              <LanguageSwitcher />
              <Cart />
            </div>
          </>
        )}
      </ul>
    </nav>
  );
}
