import React, { useState } from "react";
import {
  Typography,
  TextField,
  Button,
  Box,
  Container,
  Alert,
  Link,
  Paper,
  Avatar,
} from "@mui/material";
import { LockResetOutlined } from "@mui/icons-material";
import { useTranslation } from "react-i18next";
import AuthPage from "./AuthPage";
import { forgotPassword } from "../../services/Auth";

export default function ForgotPasswordPage() {
  const { t } = useTranslation();
  const [email, setEmail] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setLoading(true);

    try {
      const response = await forgotPassword(email);
      setSuccess(response.message || t("auth.reset_email_sent"));
    } catch (err: any) {
      setError(err.response?.data?.detail || t("auth.error_generic"));
    } finally {
      setLoading(false);
    }
  };

  return (
    <AuthPage>
      <Container maxWidth="xs">
        <Paper
          elevation={6}
          sx={{
            mt: 4,
            borderRadius: 3,
            overflow: "hidden",
          }}
        >
          {/* Gradient Header */}
          <Box
            sx={{
              background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
              py: 4,
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            <Avatar
              sx={{
                bgcolor: "rgba(255,255,255,0.2)",
                width: 56,
                height: 56,
                mb: 1,
              }}
            >
              <LockResetOutlined sx={{ fontSize: 30, color: "white" }} />
            </Avatar>
            <Typography component="h1" variant="h5" sx={{ color: "white", fontWeight: 600 }}>
              {t("auth.forgot_password")}
            </Typography>
            <Typography variant="body2" sx={{ color: "rgba(255,255,255,0.8)", mt: 0.5, textAlign: "center", px: 2 }}>
              {t("auth.forgot_password_desc")}
            </Typography>
          </Box>

          {/* Form */}
          <Box
            component="form"
            onSubmit={handleSubmit}
            sx={{ p: 4 }}
          >
            {error && (
              <Alert severity="error" sx={{ mb: 2 }}>
                {error}
              </Alert>
            )}

            {success && (
              <Alert severity="success" sx={{ mb: 2 }}>
                {success}
              </Alert>
            )}

            <TextField
              margin="normal"
              required
              fullWidth
              id="email"
              label={t("auth.email")}
              name="email"
              autoComplete="email"
              autoFocus
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              sx={{ mb: 2 }}
            />

            <Button
              type="submit"
              fullWidth
              variant="contained"
              size="large"
              disabled={loading || !email}
              sx={{
                py: 1.5,
                background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
                "&:hover": {
                  background: "linear-gradient(135deg, #5a6fd6 0%, #6a4190 100%)",
                },
              }}
            >
              {loading ? t("common.loading") : t("auth.send_reset_link")}
            </Button>

            <Box sx={{ textAlign: "center", mt: 3 }}>
              <Link href="/login" underline="hover" sx={{ color: "#667eea" }}>
                {t("auth.back_to_login")}
              </Link>
            </Box>
          </Box>
        </Paper>
      </Container>
    </AuthPage>
  );
}
