import api from "./api";

// Types
export interface IShippingInfo {
  name: string;
  phone: string;
  email: string;
  address: string;
  note?: string;
}

export interface ICreateOrderRequest {
  shipping: IShippingInfo;
}

export interface IOrderItem {
  id: number;
  product_id: number;
  product_name: string;
  product_image: string | null;
  product_size: string | null;
  quantity: number;
  unit_price: number;
  total_price: number;
}

export interface IOrder {
  id: number;
  user_id: string;
  shipping_name: string;
  shipping_phone: string;
  shipping_email: string;
  shipping_address: string;
  subtotal: number;
  shipping_fee: number;
  total_amount: number;
  status: string;
  note: string | null;
  items: IOrderItem[];
  created_at: string;
  updated_at: string;
}

export interface IOrderListItem {
  id: number;
  total_amount: number;
  status: string;
  items_count: number;
  created_at: string;
}

// Create order from cart
export const createOrder = async (request: ICreateOrderRequest): Promise<IOrder> => {
  const response = await api.post("/orders", request);
  return response.data;
};

// Get user's orders
export const getMyOrders = async (): Promise<IOrderListItem[]> => {
  const response = await api.get("/orders");
  return response.data;
};

// Get order detail
export const getOrderDetail = async (orderId: number): Promise<IOrder> => {
  const response = await api.get(`/orders/${orderId}`);
  return response.data;
};

// Export as service object for convenience
export const orderService = {
  createOrder,
  getMyOrders,
  getOrderDetail,
};

export default orderService;
