import React, { useState } from "react";
import {
  Container,
  Typography,
  TextField,
  Button,
  Box,
  Card,
  CardContent,
  Alert,
  CircularProgress,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  Divider,
} from "@mui/material";
import { uploadProductImage, createProduct, ICreateProduct } from "../../services/Product";
import { PRODUCT_CATEGORIES } from "../../constants";

const PRODUCT_TYPES = PRODUCT_CATEGORIES;

export default function AddProduct() {
  const [formData, setFormData] = useState<ICreateProduct>({
    slug: "",
    product_type: "",
    product_name: "",
    price: 0,
    stock: 0,
    blurb: "",
    description: "",
    image_url: "",
    sale_price: undefined,
  });

  // Supplement-specific fields
  const [supplementInfo, setSupplementInfo] = useState({
    serving_size: "",
    servings_per_container: "",
    ingredients: "",
    usage_instructions: "",
    warnings: "",
    expiry_date: "",
    manufacturer: "",
    country_of_origin: "",
    certification: "",
  });

  const [imageFile, setImageFile] = useState<File | null>(null);
  const [imagePreview, setImagePreview] = useState<string>("");
  const [uploading, setUploading] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState("");

  // Field-level validation errors
  const [fieldErrors, setFieldErrors] = useState<Record<string, string>>({});

  // Clear field error when user starts typing
  const clearFieldError = (fieldName: string) => {
    if (fieldErrors[fieldName]) {
      setFieldErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[fieldName];
        return newErrors;
      });
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;

    // Clear error for this field
    clearFieldError(name);

    setFormData((prev) => ({
      ...prev,
      [name]: ["price", "sale_price", "stock"].includes(name) ? Number(value) : value,
    }));
  };

  const handleSupplementChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setSupplementInfo((prev) => ({
      ...prev,
      [name]: name === "servings_per_container" ? (value ? Number(value) : "") : value,
    }));
  };

  const handleTypeChange = (e: any) => {
    setFormData((prev) => ({
      ...prev,
      product_type: e.target.value,
    }));
  };

  const generateSlug = () => {
    const slug = formData.product_name
      .toLowerCase()
      .replace(/[^a-z0-9\s-]/g, "")
      .replace(/\s+/g, "-")
      .replace(/-+/g, "-")
      .trim();
    setFormData((prev) => ({ ...prev, slug }));
  };

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setImageFile(file);
      setImagePreview(URL.createObjectURL(file));
    }
  };

  const handleUploadImage = async () => {
    if (!imageFile) return;

    setUploading(true);
    setError("");

    const result = await uploadProductImage(imageFile);

    if (result && result.url) {
      setFormData((prev) => ({
        ...prev,
        image_url: result.url,
      }));
      setSuccess(false);
    } else {
      setError("Failed to upload image");
    }

    setUploading(false);
  };

  const validateForm = (): string | null => {
    // Required fields
    if (!formData.product_name.trim()) return "Product name is required";
    if (!formData.slug.trim()) return "Slug is required";
    if (!formData.product_type) return "Product type is required";
    if (!formData.blurb?.trim()) return "Short description is required";
    if (!formData.image_url) return "Product image is required";

    // Price validations
    if (formData.price <= 0) return "Price must be greater than 0";
    if (formData.sale_price !== undefined && formData.sale_price < 0) {
      return "Sale price cannot be negative";
    }
    if (formData.sale_price !== undefined && formData.sale_price > formData.price) {
      return "Sale price cannot be higher than regular price";
    }

    // Stock validation
    if (formData.stock < 0) return "Stock cannot be negative";

    // Expiry date validation (if provided)
    if (supplementInfo.expiry_date) {
      const expiryDate = new Date(supplementInfo.expiry_date);
      const today = new Date();
      today.setHours(0, 0, 0, 0);

      if (expiryDate <= today) {
        return "Expiry date must be in the future";
      }
    }

    return null;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // Validate form
    const validationError = validateForm();
    if (validationError) {
      setError(validationError);
      return;
    }

    setSubmitting(true);
    setError("");

    const productData: any = {
      slug: formData.slug,
      product_type: formData.product_type,
      product_name: formData.product_name,
      price: formData.price,
      stock: formData.stock,
      blurb: formData.blurb,
      description: formData.description || "",
      image_url: formData.image_url,
    };

    if (formData.sale_price !== undefined && formData.sale_price > 0) {
      productData.sale_price = formData.sale_price;
    }

    // Add supplement info (only if provided)
    if (supplementInfo.serving_size) productData.serving_size = supplementInfo.serving_size;
    if (supplementInfo.servings_per_container) productData.servings_per_container = Number(supplementInfo.servings_per_container);
    if (supplementInfo.ingredients) productData.ingredients = supplementInfo.ingredients;
    if (supplementInfo.usage_instructions) productData.usage_instructions = supplementInfo.usage_instructions;
    if (supplementInfo.warnings) productData.warnings = supplementInfo.warnings;
    if (supplementInfo.expiry_date) productData.expiry_date = supplementInfo.expiry_date;
    if (supplementInfo.manufacturer) productData.manufacturer = supplementInfo.manufacturer;
    if (supplementInfo.country_of_origin) productData.country_of_origin = supplementInfo.country_of_origin;
    if (supplementInfo.certification) productData.certification = supplementInfo.certification;

    const result = await createProduct(productData);

    if (result) {
      setSuccess(true);
      // Reset form
      setFormData({
        slug: "",
        product_type: "",
        product_name: "",
        price: 0,
        stock: 0,
        blurb: "",
        description: "",
        image_url: "",
        sale_price: undefined,
      });
      setSupplementInfo({
        serving_size: "",
        servings_per_container: "",
        ingredients: "",
        usage_instructions: "",
        warnings: "",
        expiry_date: "",
        manufacturer: "",
        country_of_origin: "",
        certification: "",
      });
      setImageFile(null);
      setImagePreview("");

      // Scroll to top to see success message
      window.scrollTo({ top: 0, behavior: 'smooth' });
    } else {
      setError("Failed to create product");
    }

    setSubmitting(false);
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Typography variant="h4" component="h1" sx={{ mb: 4, fontWeight: 600 }}>
        Add New Supplement Product
      </Typography>

      {success && (
        <Alert severity="success" sx={{ mb: 3 }} onClose={() => setSuccess(false)}>
          Product created successfully!
        </Alert>
      )}

      {error && (
        <Alert severity="error" sx={{ mb: 3 }} onClose={() => setError("")}>
          {error}
        </Alert>
      )}

      <Card>
        <CardContent>
          <Box component="form" onSubmit={handleSubmit}>

            {/* Basic Information */}
            <Typography variant="h6" sx={{ mb: 2, fontWeight: 600, color: "#1976d2" }}>
              Basic Information
            </Typography>

            <TextField
              fullWidth
              required
              label="Product Name"
              name="product_name"
              value={formData.product_name}
              onChange={handleInputChange}
              sx={{ mb: 3 }}
              placeholder="e.g., Omega-3 Fish Oil 1000mg"
            />

            <Box sx={{ display: "flex", gap: 2, mb: 3 }}>
              <TextField
                fullWidth
                required
                label="Slug"
                name="slug"
                value={formData.slug}
                onChange={handleInputChange}
                helperText="URL-friendly identifier (SEO)"
                placeholder="omega-3-fish-oil-1000mg"
              />
              <Button
                variant="outlined"
                onClick={generateSlug}
                sx={{ whiteSpace: "nowrap", minWidth: 140 }}
              >
                Auto Generate
              </Button>
            </Box>

            <FormControl fullWidth required sx={{ mb: 3 }}>
              <InputLabel>Product Type</InputLabel>
              <Select
                value={formData.product_type}
                label="Product Type"
                onChange={handleTypeChange}
              >
                {PRODUCT_TYPES.map((type) => (
                  <MenuItem key={type} value={type}>
                    {type}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            <TextField
              fullWidth
              label="Manufacturer"
              name="manufacturer"
              value={supplementInfo.manufacturer}
              onChange={handleSupplementChange}
              sx={{ mb: 3 }}
              placeholder="e.g., Nature's Bounty, NOW Foods"
            />

            <TextField
              fullWidth
              label="Country of Origin"
              name="country_of_origin"
              value={supplementInfo.country_of_origin}
              onChange={handleSupplementChange}
              sx={{ mb: 3 }}
              placeholder="e.g., USA, Vietnam, Germany"
            />

            <Divider sx={{ my: 4 }} />

            {/* Supplement Facts */}
            <Typography variant="h6" sx={{ mb: 2, fontWeight: 600, color: "#1976d2" }}>
              Supplement Facts
            </Typography>

            <Box sx={{ display: "flex", gap: 2, mb: 3 }}>
              <TextField
                fullWidth
                label="Serving Size"
                name="serving_size"
                value={supplementInfo.serving_size}
                onChange={handleSupplementChange}
                placeholder="e.g., 2 capsules, 1 scoop (30g)"
              />
              <TextField
                fullWidth
                label="Servings per Container"
                name="servings_per_container"
                type="number"
                value={supplementInfo.servings_per_container}
                onChange={handleSupplementChange}
                InputProps={{ inputProps: { min: 0, step: 1 } }}
                placeholder="e.g., 30, 60, 90"
              />
            </Box>

            <TextField
              fullWidth
              label="Ingredients"
              name="ingredients"
              value={supplementInfo.ingredients}
              onChange={handleSupplementChange}
              multiline
              rows={4}
              sx={{ mb: 3 }}
              placeholder="List all active and inactive ingredients (comma-separated)"
              helperText="e.g., Fish Oil Concentrate, Gelatin, Glycerin, Vitamin E"
            />

            <TextField
              fullWidth
              label="Usage Instructions"
              name="usage_instructions"
              value={supplementInfo.usage_instructions}
              onChange={handleSupplementChange}
              multiline
              rows={3}
              sx={{ mb: 3 }}
              placeholder="How to use this supplement"
              helperText="e.g., Take 2 capsules daily with meals, or as directed by healthcare professional"
            />

            <TextField
              fullWidth
              label="Warnings"
              name="warnings"
              value={supplementInfo.warnings}
              onChange={handleSupplementChange}
              multiline
              rows={3}
              sx={{ mb: 3 }}
              placeholder="Contraindications, side effects, precautions"
              helperText="e.g., Not for pregnant/nursing women. Consult doctor if taking medication."
            />

            <TextField
              fullWidth
              label="Certifications"
              name="certification"
              value={supplementInfo.certification}
              onChange={handleSupplementChange}
              sx={{ mb: 3 }}
              placeholder="e.g., GMP, FDA, ISO 22000, Halal, Organic"
              helperText="Comma-separated list of certifications"
            />

            <TextField
              fullWidth
              required
              label="Expiry Date"
              name="expiry_date"
              type="date"
              value={supplementInfo.expiry_date}
              onChange={handleSupplementChange}
              InputLabelProps={{ shrink: true }}
              sx={{ mb: 3 }}
              helperText="Must be a future date"
            />

            <Divider sx={{ my: 4 }} />

            {/* Pricing & Inventory */}
            <Typography variant="h6" sx={{ mb: 2, fontWeight: 600, color: "#1976d2" }}>
              Pricing & Inventory
            </Typography>

            <Box sx={{ display: "flex", gap: 2, mb: 3 }}>
              <TextField
                fullWidth
                required
                label="Price"
                name="price"
                type="number"
                value={formData.price || ""}
                onChange={handleInputChange}
                InputProps={{ inputProps: { min: 0, step: 0.01 } }}
                helperText="Regular price (must be > 0)"
              />
              <TextField
                fullWidth
                label="Sale Price"
                name="sale_price"
                type="number"
                value={formData.sale_price || ""}
                onChange={handleInputChange}
                InputProps={{ inputProps: { min: 0, step: 0.01 } }}
                helperText="Leave empty if no sale"
              />
              <TextField
                fullWidth
                required
                label="Stock"
                name="stock"
                type="number"
                value={formData.stock || ""}
                onChange={handleInputChange}
                InputProps={{ inputProps: { min: 0, step: 1 } }}
                helperText="Total quantity available"
              />
            </Box>

            <Divider sx={{ my: 4 }} />

            {/* Content & Media */}
            <Typography variant="h6" sx={{ mb: 2, fontWeight: 600, color: "#1976d2" }}>
              Content & Media
            </Typography>

            <TextField
              fullWidth
              required
              label="Short Description"
              name="blurb"
              value={formData.blurb}
              onChange={handleInputChange}
              multiline
              rows={2}
              sx={{ mb: 3 }}
              placeholder="Brief product summary (appears in product cards)"
              helperText="Keep it concise - 1-2 sentences"
            />

            <TextField
              fullWidth
              label="Full Description"
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              multiline
              rows={6}
              sx={{ mb: 3 }}
              placeholder="Detailed product information, benefits, research, etc."
              helperText="Include benefits, scientific backing, target audience"
            />

            {/* Product Image Upload */}
            <Box sx={{ mb: 3 }}>
              <Typography variant="subtitle1" fontWeight={600} sx={{ mb: 2 }}>
                Product Image *
              </Typography>

              <input
                type="file"
                accept="image/*"
                onChange={handleImageChange}
                style={{ marginBottom: "16px", display: "block" }}
              />

              {imageFile && !formData.image_url && (
                <Button
                  variant="contained"
                  onClick={handleUploadImage}
                  disabled={uploading}
                  sx={{ mb: 2 }}
                >
                  {uploading ? <CircularProgress size={24} /> : "Upload to Cloudinary"}
                </Button>
              )}

              {formData.image_url && (
                <Alert severity="success" sx={{ mb: 2 }}>
                  Image uploaded successfully!
                </Alert>
              )}

              {imagePreview && (
                <Box
                  component="img"
                  src={imagePreview}
                  alt="Product preview"
                  sx={{
                    maxWidth: "100%",
                    maxHeight: 300,
                    objectFit: "contain",
                    border: "1px solid #ddd",
                    borderRadius: 1,
                    p: 1,
                  }}
                />
              )}
            </Box>

            <Divider sx={{ my: 4 }} />

            {/* Submit Button */}
            <Box sx={{ display: "flex", gap: 2, justifyContent: "flex-end" }}>
              <Button
                variant="outlined"
                onClick={() => window.history.back()}
              >
                Cancel
              </Button>
              <Button
                type="submit"
                variant="contained"
                size="large"
                disabled={submitting || !formData.image_url}
                sx={{ minWidth: 150 }}
              >
                {submitting ? <CircularProgress size={24} /> : "Create Product"}
              </Button>
            </Box>
          </Box>
        </CardContent>
      </Card>
    </Container>
  );
}
