import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useTranslation } from "react-i18next";

import { getProductInfo } from "../../services/Product";
import { getProductReviews, createProductReview, IReviewListResponse, ICreateReview } from "../../services/Review";
import { useCurrency } from "../../context/CurrencyContext";
import { useAuth } from "../../context/AuthContext";

import AddToCart from "../../components/ui/buttons/AddToCart/AddToCart";
import RadioProduct from "../../components/ui/buttons/RadioProduct/RadioProduct";

import {
  Typography,
  TextField,
  Button,
  Box,
  Paper,
  Skeleton,
  Rating,
  Divider,
  Chip,
  IconButton,
  Breadcrumbs,
  Link,
  Tabs,
  Tab,
  Avatar,
} from "@mui/material";
import {
  Home,
  FavoriteBorder,
  Share,
  LocalShipping,
  Verified,
  Loop,
  Security,
} from "@mui/icons-material";
import { getImageUrl } from "../../utils/imageUtils";

import { Product } from "../../types/types";

export default function ProductPage() {
  const { productID } = useParams();
  const { t } = useTranslation();
  const navigate = useNavigate();
  const { formatPrice } = useCurrency();
  const { isLoggedIn } = useAuth();
  const [productInfo, setProductInfo] = useState<Product>();
  const [color, setColor] = useState<string>("");
  const [size, setSize] = useState<string>("");
  const [loading, setLoading] = useState<boolean>(true);
  const [selectedImage, setSelectedImage] = useState<number>(0);
  const [tabValue, setTabValue] = useState(0);

  // Review states
  const [reviews, setReviews] = useState<IReviewListResponse | null>(null);
  const [reviewRating, setReviewRating] = useState<number>(5);
  const [reviewContent, setReviewContent] = useState<string>("");
  const [submittingReview, setSubmittingReview] = useState<boolean>(false);
  const [reviewError, setReviewError] = useState<string>("");
  const [reviewSuccess, setReviewSuccess] = useState<boolean>(false);

  // Get product images: main image + color images
  const productImages = React.useMemo(() => {
    if (!productInfo) return [];
    const images = [productInfo.image_url];
    if (productInfo.colors && productInfo.colors.length > 0) {
      productInfo.colors.forEach(c => {
        if (c.image_url) images.push(c.image_url);
      });
    }
    return images;
  }, [productInfo]);

  // Get current color image
  const currentColorImage = React.useMemo(() => {
    if (!productInfo?.colors || !color) return productInfo?.image_url;
    const selectedColor = productInfo.colors.find(c => c.color.toLowerCase() === color.toLowerCase());
    return selectedColor?.image_url || productInfo.image_url;
  }, [productInfo, color]);

  React.useEffect(() => {
    const fetchProduct = async () => {
      if (productID) {
        const response = await getProductInfo(productID);
        const product = new Product(response);
        setProductInfo(product);

        // Set default color and size
        if (product.colors && product.colors.length > 0) {
          setColor(product.colors[0].color);
        }
        if (product.sizes && product.sizes.length > 0) {
          setSize(product.sizes[0].size);
        }

        setLoading(false);
      }
    };

    fetchProduct();
  }, [productID]);

  // Update main image when color changes
  React.useEffect(() => {
    if (color && productInfo?.colors) {
      const colorIndex = productInfo.colors.findIndex(c => c.color.toLowerCase() === color.toLowerCase());
      if (colorIndex !== -1 && productInfo.colors[colorIndex].image_url) {
        // Set to color image (index 1+ in productImages array)
        setSelectedImage(colorIndex + 1);
      } else {
        // Fallback to main image
        setSelectedImage(0);
      }
    }
  }, [color, productInfo]);

  const discountPercent = productInfo?.sale_price
    ? Math.round(((productInfo.price - productInfo.sale_price) / productInfo.price) * 100)
    : 0;

  return (
    <Box sx={{ bgcolor: "#f5f5f5", minHeight: "100vh" }}>
      {/* Breadcrumbs */}
      <Box sx={{ bgcolor: "white", py: 2, borderBottom: "1px solid #eee" }}>
        <Box className="container mx-auto px-4">
          <Breadcrumbs>
            <Link
              underline="hover"
              color="inherit"
              sx={{ display: "flex", alignItems: "center", cursor: "pointer" }}
              onClick={() => navigate("/")}
            >
              <Home sx={{ mr: 0.5 }} fontSize="small" />
              {t("nav.home")}
            </Link>
            <Link
              underline="hover"
              color="inherit"
              sx={{ cursor: "pointer" }}
              onClick={() => navigate("/products")}
            >
              {t("nav.products")}
            </Link>
            {productInfo && (
              <Typography color="text.primary">{productInfo.product_name}</Typography>
            )}
          </Breadcrumbs>
        </Box>
      </Box>

      <Box className="container mx-auto px-4 py-6">
        {loading ? (
          <Paper sx={{ p: 4, borderRadius: 2 }}>
            <Box sx={{ display: "flex", gap: 4, flexWrap: { xs: "wrap", md: "nowrap" } }}>
              <Box sx={{ width: { xs: "100%", md: "50%" } }}>
                <Skeleton variant="rectangular" height={500} sx={{ borderRadius: 2 }} />
                <Box sx={{ display: "flex", gap: 1, mt: 2 }}>
                  {[1, 2, 3].map((i) => (
                    <Skeleton key={i} variant="rectangular" width={80} height={80} sx={{ borderRadius: 1 }} />
                  ))}
                </Box>
              </Box>
              <Box sx={{ flex: 1 }}>
                <Skeleton variant="text" height={40} width="80%" />
                <Skeleton variant="text" height={30} width="40%" />
                <Skeleton variant="text" height={50} width="30%" sx={{ my: 2 }} />
                <Skeleton variant="rectangular" height={100} />
              </Box>
            </Box>
          </Paper>
        ) : productInfo ? (
          <>
            {/* Main Product Section */}
            <Paper sx={{ p: { xs: 2, md: 4 }, borderRadius: 2, mb: 3 }}>
              <Box sx={{ display: "flex", gap: 4, flexWrap: { xs: "wrap", md: "nowrap" } }}>
                {/* Product Images */}
                <Box sx={{ width: { xs: "100%", md: "50%" } }}>
                  <Box
                    sx={{
                      position: "relative",
                      borderRadius: 2,
                      overflow: "hidden",
                      bgcolor: "#f8f8f8",
                      mb: 2,
                    }}
                  >
                    <img
                      src={getImageUrl(currentColorImage || productImages[selectedImage])}
                      alt={productInfo.product_name}
                      style={{
                        width: "100%",
                        height: "500px",
                        objectFit: "cover",
                      }}
                    />
                    {productInfo.sale_price && (
                      <Chip
                        label={`-${discountPercent}%`}
                        color="error"
                        sx={{
                          position: "absolute",
                          top: 16,
                          left: 16,
                          fontWeight: 600,
                        }}
                      />
                    )}
                    <Box sx={{ position: "absolute", top: 16, right: 16, display: "flex", flexDirection: "column", gap: 1 }}>
                      <IconButton sx={{ bgcolor: "white", "&:hover": { bgcolor: "#f5f5f5" } }}>
                        <FavoriteBorder />
                      </IconButton>
                      <IconButton sx={{ bgcolor: "white", "&:hover": { bgcolor: "#f5f5f5" } }}>
                        <Share />
                      </IconButton>
                    </Box>
                  </Box>
                  {/* Thumbnail images */}
                  <Box sx={{ display: "flex", gap: 1 }}>
                    {productImages.map((img, idx) => (
                      <Box
                        key={idx}
                        onClick={() => setSelectedImage(idx)}
                        sx={{
                          width: 80,
                          height: 80,
                          borderRadius: 1,
                          overflow: "hidden",
                          cursor: "pointer",
                          border: selectedImage === idx ? "2px solid #1976d2" : "2px solid transparent",
                          opacity: selectedImage === idx ? 1 : 0.6,
                          transition: "all 0.2s ease",
                          "&:hover": { opacity: 1 },
                        }}
                      >
                        <img
                          src={getImageUrl(img)}
                          alt={`${productInfo.product_name} ${idx + 1}`}
                          style={{ width: "100%", height: "100%", objectFit: "cover" }}
                        />
                      </Box>
                    ))}
                  </Box>
                </Box>

                {/* Product Info */}
                <Box sx={{ flex: 1 }}>
                  <Typography variant="h4" fontWeight={600} gutterBottom>
                    {productInfo.product_name}
                  </Typography>

                  <Box sx={{ display: "flex", alignItems: "center", gap: 2, mb: 2 }}>
                    <Rating value={4.5} precision={0.5} readOnly />
                    <Typography variant="body2" color="text.secondary">
                      (128 {t("review.title")})
                    </Typography>
                    <Chip
                      icon={<Verified sx={{ fontSize: 16 }} />}
                      label={t("product.in_stock")}
                      color="success"
                      size="small"
                      variant="outlined"
                    />
                  </Box>

                  {/* Price */}
                  <Box sx={{ mb: 3 }}>
                    {productInfo.sale_price ? (
                      <Box sx={{ display: "flex", alignItems: "center", gap: 2 }}>
                        <Typography variant="h4" color="error" fontWeight={700}>
                          {formatPrice(productInfo.sale_price)}
                        </Typography>
                        <Typography
                          variant="h6"
                          sx={{ textDecoration: "line-through", color: "#999" }}
                        >
                          {formatPrice(productInfo.price)}
                        </Typography>
                        <Chip
                          label={`-${Math.round(((productInfo.price - productInfo.sale_price) / productInfo.price) * 100)}%`}
                          color="error"
                          size="small"
                        />
                      </Box>
                    ) : (
                      <Typography variant="h4" fontWeight={700}>
                        {formatPrice(productInfo.price)}
                      </Typography>
                    )}
                  </Box>

                  <Typography color="text.secondary" sx={{ mb: 3, lineHeight: 1.8 }}>
                    {productInfo.blurb}
                  </Typography>

                  <Divider sx={{ my: 3 }} />

                  {/* Color Selection */}
                  {productInfo.colors && productInfo.colors.length > 0 && (
                    <Box sx={{ mb: 3 }}>
                      <Typography variant="subtitle2" fontWeight={600} sx={{ mb: 1.5 }}>
                        {t("product.color")}: <span style={{ fontWeight: 400 }}>{color}</span>
                      </Typography>
                      <RadioProduct 
                        value={productInfo.colors.map(c => c.color)} 
                        setState={setColor} 
                      />
                    </Box>
                  )}

                  {/* Size Selection */}
                  {productInfo.sizes && productInfo.sizes.length > 0 && (
                    <Box sx={{ mb: 3 }}>
                      <Typography variant="subtitle2" fontWeight={600} sx={{ mb: 1.5 }}>
                        {t("product.size")}: <span style={{ fontWeight: 400 }}>{size.toUpperCase()}</span>
                      </Typography>
                      <RadioProduct 
                        value={productInfo.sizes.map(s => s.size)} 
                        setState={setSize} 
                      />
                    </Box>
                  )}

                  <Divider sx={{ my: 3 }} />

                  {/* Add to Cart */}
                  <Box sx={{ mb: 3 }}>
                    <AddToCart product={productInfo} size={size} color={color} />
                  </Box>

                  {/* Trust badges */}
                  <Box sx={{ display: "flex", gap: 3, flexWrap: "wrap" }}>
                    <Box sx={{ display: "flex", alignItems: "center", gap: 1 }}>
                      <LocalShipping color="primary" />
                      <Typography variant="body2">{t("general.free_shipping")}</Typography>
                    </Box>
                    <Box sx={{ display: "flex", alignItems: "center", gap: 1 }}>
                      <Loop color="primary" />
                      <Typography variant="body2">30-Day Returns</Typography>
                    </Box>
                    <Box sx={{ display: "flex", alignItems: "center", gap: 1 }}>
                      <Security color="primary" />
                      <Typography variant="body2">Secure Payment</Typography>
                    </Box>
                  </Box>
                </Box>
              </Box>
            </Paper>

            {/* Description & Reviews Tabs */}
            <Paper sx={{ borderRadius: 2 }}>
              <Tabs
                value={tabValue}
                onChange={(_, newValue) => setTabValue(newValue)}
                sx={{ borderBottom: "1px solid #eee", px: 3 }}
              >
                <Tab label={t("product.description")} />
                <Tab label={`${t("review.title")} (128)`} />
              </Tabs>

              <Box sx={{ p: 3 }}>
                {tabValue === 0 && (
                  <Typography color="text.secondary" sx={{ lineHeight: 1.8 }}>
                    Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.
                    Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.
                    Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                    <br /><br />
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                  </Typography>
                )}

                {tabValue === 1 && (
                  <Box>
                    {/* Review Form */}
                    <Box sx={{ mb: 4 }}>
                      <Typography variant="h6" fontWeight={600} gutterBottom>
                        {t("review.write_review")}
                      </Typography>
                      <Box sx={{ display: "flex", alignItems: "center", gap: 2, mb: 2 }}>
                        <Typography>{t("review.rating")}:</Typography>
                        <Rating size="large" />
                      </Box>
                      <TextField
                        multiline
                        rows={4}
                        placeholder={t("review.comment")}
                        fullWidth
                        sx={{ mb: 2 }}
                      />
                      <Button variant="contained" sx={{ textTransform: "none" }}>
                        {t("common.submit")}
                      </Button>
                    </Box>

                    <Divider sx={{ my: 3 }} />

                    {/* Mock Reviews */}
                    <Typography variant="h6" fontWeight={600} gutterBottom>
                      Customer Reviews
                    </Typography>
                    {[1, 2, 3].map((review) => (
                      <Box key={review} sx={{ py: 2, borderBottom: "1px solid #eee" }}>
                        <Box sx={{ display: "flex", gap: 2 }}>
                          <Avatar>U</Avatar>
                          <Box sx={{ flex: 1 }}>
                            <Box sx={{ display: "flex", justifyContent: "space-between", mb: 1 }}>
                              <Typography fontWeight={600}>User {review}</Typography>
                              <Typography variant="caption" color="text.secondary">
                                2 days ago
                              </Typography>
                            </Box>
                            <Rating value={5} size="small" readOnly sx={{ mb: 1 }} />
                            <Typography color="text.secondary">
                              Great product! Exactly as described. Fast shipping and excellent quality.
                            </Typography>
                          </Box>
                        </Box>
                      </Box>
                    ))}
                  </Box>
                )}
              </Box>
            </Paper>
          </>
        ) : (
          <Paper sx={{ p: 8, textAlign: "center", borderRadius: 2 }}>
            <Typography variant="h5" fontWeight={600} gutterBottom>
              {t("product.not_found")}
            </Typography>
            <Button variant="contained" onClick={() => navigate("/products")}>
              {t("cart.continue_shopping")}
            </Button>
          </Paper>
        )}
      </Box>
    </Box>
  );
}
