import React from "react";
import { Link } from "react-router-dom";
import {
  Box,
  Container,
  Grid,
  Typography,
  IconButton,
  TextField,
  Button,
  Divider,
} from "@mui/material";
import {
  Facebook,
  Instagram,
  Twitter,
  YouTube,
  Email,
  Phone,
  LocationOn,
} from "@mui/icons-material";

export default function Footer() {
  const currentYear = new Date().getFullYear();

  const quickLinks = [
    { label: "Home", href: "/" },
    { label: "Products", href: "/products" },
    { label: "New Arrivals", href: "/products?category=new-arrivals" },
    { label: "Sale", href: "/products?sale=true" },
  ];

  const customerService = [
    { label: "Contact Us", href: "/contact" },
    { label: "FAQ", href: "/faq" },
    { label: "Shipping Info", href: "/shipping" },
    { label: "Returns & Exchanges", href: "/returns" },
  ];

  const categories = [
    { label: "Tops", href: "/products?type=Tops" },
    { label: "Bottoms", href: "/products?type=Bottoms" },
    { label: "Shoes", href: "/products?type=Shoes" },
    { label: "Accessories", href: "/products?type=Accessories" },
  ];

  return (
    <footer>
      {/* Newsletter Section */}
      <Box
        sx={{
          background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
          py: { xs: 4, sm: 6 },
        }}
      >
        <Container maxWidth="md" sx={{ px: { xs: 2, sm: 3 } }}>
          <Box sx={{ textAlign: "center" }}>
            <Typography
              sx={{ color: "white", fontWeight: 700, mb: 1, fontSize: { xs: "1.5rem", sm: "2rem" } }}
            >
              Subscribe to Our Newsletter
            </Typography>
            <Typography
              sx={{ color: "rgba(255,255,255,0.85)", mb: { xs: 2, sm: 3 }, fontSize: { xs: "0.85rem", sm: "1rem" } }}
            >
              Get the latest updates on new products and upcoming sales
            </Typography>
            <Box
              sx={{
                display: "flex",
                gap: 1,
                maxWidth: 500,
                mx: "auto",
                flexDirection: { xs: "column", sm: "row" },
              }}
            >
              <TextField
                placeholder="Enter your email"
                variant="outlined"
                size="small"
                fullWidth
                sx={{
                  bgcolor: "white",
                  borderRadius: { xs: 1, sm: 2 },
                  "& .MuiOutlinedInput-root": {
                    borderRadius: { xs: 1, sm: 2 },
                  },
                }}
              />
              <Button
                variant="contained"
                sx={{
                  bgcolor: "#ffd93d",
                  color: "#1a1a2e",
                  fontWeight: 600,
                  px: { xs: 3, sm: 4 },
                  whiteSpace: "nowrap",
                  fontSize: { xs: "0.85rem", sm: "1rem" },
                  "&:hover": {
                    bgcolor: "#ffcd00",
                  },
                }}
              >
